import React, { useState } from "react";

function TeamTile(props) {

  const teams = props.teams;
  const [selectedTeam, setSelectedTeam] = useState(null);

  if (!teams) {
    return "Data Not Fetched";
  }

  const handleButton = (event) => {
    event.preventDefault(); // Prevent default form submission behavior
    const teamLink = `/team/${selectedTeam}/`;
    window.location.href = teamLink;
  };

  return (
    <div>

        <label for="team">Choose team: </label>
          <select
            name="team"
            id="team"
            onChange={(event) => setSelectedTeam(event.target.value)}
          >
            {teams.map((team) => (
              <option key={team.teamName} value={team.teamName}>{team.teamName}</option>
                    )
                )
            }
          </select>

          <button type="submit" onClick={handleButton}>Submit</button>

          <p>{selectedTeam}</p>

    </div>
  );
}

export default TeamTile;
