import React,{ useState, useEffect } from "react";
import {useParams, Link } from "react-router-dom";
import "../styling/seasonPage.css"

function SeasonTile(){

    const [year, setYear] = useState('');
    const {selectedYear} = useParams();

    useEffect(
        () => {
          const fetchYear = async () => {
            const response = await fetch(`http://localhost:8081/dashboard/allSeasons`);
            const data = await response.json();
            setYear(data);
          }

        fetchYear();

        }, []
      );

      if(!year){return null}

    return(
        <div className="seasonTile">

            <h2>Seasons</h2>

            {year.map(y => <p className={{selectedYear} === y ? "spara" : "para"}><Link to = {`/season/${y}`}>{y}</Link></p>)}

        </div>
    );
}

export default SeasonTile;
