import "../styling/homePage.css";
import React, { useState, useEffect } from "react";

function HomePage() {
  const [teams, setTeams] = useState();
  const [selectedTeam1, setSelectedTeam1] = useState(null);
  const [selectedTeam2, setSelectedTeam2] = useState(null);

  useEffect(() => {
    const fetchTeams = async () => {
      const responce = await fetch("http://localhost:8081/dashboard/allTeams");
      const data = await responce.json();
      setTeams(data);
    };
    fetchTeams();
  }, []);

  if (!teams) {
    return "Data Not Fetched";
  }

  const handleMatch = (event) => {
    event.preventDefault(); // Prevent default form submission behavior
    const teamLink = `/match/${selectedTeam1}/${selectedTeam2}`;
    window.location.href = teamLink;
  };

  return (
    <div>

        <h1>Find Matches between: </h1>

        <label for="team1">Choose team1: </label>
          <select
            name="team1"
            id="team1"
            onChange={(event) => setSelectedTeam1(event.target.value)}
          >
            {teams.map((team) => (
              <option key={team.teamName} value={team.teamName}>
                {team.teamName}
              </option>
            ))}
          </select>

        <label for="team2">Choose team2: </label>
          <select 
          name="team2" 
          id="team2"
          onChange={(event) => setSelectedTeam2(event.target.value)}
          >
            {teams
              .filter((team) => team.teamName !== selectedTeam1)
              .map((team) => (
                <option key={team.teamName} value={team.teamName}>
                  {team.teamName}
                </option>
              ))}
          </select>

          {/* <input type="submit" value="Submit"></input> */}

          <button type="submit" onClick={handleSubmit}>
        Submit
      </button>

          <p>{selectedTeam1}</p>
          <p>{selectedTeam2}</p>

    </div>
  );
}

export default HomePage;
