import MatchDetailComponent from "../components/matchDetailComponent";
import MatchSmallComponent from "../components/matchSmallComponent";
import React, { useState, useEffect } from "react";
import "./teamPage.css";
import { useParams } from "react-router-dom";

function Team() {

  const teamName = useParams();
  console.log(teamName);

  const [team, setTeam] = useState({recentMatches:[]});

  useEffect(() => {
    const fetchTeam = async () => {
      const response = await fetch(`http://localhost:8081/dashboard/team?team=${encodeURIComponent(teamName)}`);
      const data = await response.json();
      setTeam(data);
    };

    fetchTeam();
  }, [teamName]);


  console.log(`http://localhost:8081/dashboard/team?team=${teamName}`);

  if(!team || !team.recentMatches){return "Data Not Fetched"}

  return (
    <div className="teamPage">
      <div>
      <h2>Team Page</h2>
      <h4>Name: {team.teamName}</h4>
      <h4>Total Wins : {team.totalWins}</h4>
      <h4>Total Losses: {team.totalMatches - team.totalWins}</h4>
      <h4>Total Matches: {team.totalMatches}</h4></div>

      <MatchDetailComponent match = {team.recentMatches[0]}/>
      {team.recentMatches.slice(1).map( (m) => <MatchSmallComponent match={m}/>)}

    </div>
  );
}

export default Team;
