import React, {useState, useEffect} from "react";
import "../styling/matchPage.css";
import {useParams, Link} from "react-router-dom";
import MatchComponent from "../components/matchComponent";

function Match() {

  const {team1, team2} = useParams();

  const [match, setMatch] = useState('');

  useEffect(
    () => {
      const fetchMatch = async () => {
        const response = await fetch(`http://localhost:8081/dashboard/matchBetween/${team1}/${team2}`);
        const data = await response.json();
        setMatch(data);
      }

    fetchMatch();

    }, [team1, team2]
  );

  match.map()

  if(!match || !match[0]){return "Data Not Fetched"}

    return (
      <div className="matchPage">

        <h2 className="diffLinkCol">
          <Link to = {`/team/${team1}`}>{team1} </Link>
          VS
          <Link to = {`/team/${team2}`}> {team2}</Link>
        </h2>

        {
          match.map( (m) => <MatchComponent match={m}/>)
        }

      </div>
    );
  }

  export default Match;
