import React, {useState, useEffect} from "react";
import "./matchPage.css";
import {useParams, Link} from "react-router-dom";

function Match() {

  const {team1, team2} = useParams();

  const [match, setMatch] = useState('');

  useEffect(
    () => {
      const fetchMatch = async () => {
        const response = await fetch(`http://localhost:8081/dashboard/matchBetween/${team1}/${team2}`);
        const data = await response.json();
        setMatch(data);
      }

    fetchMatch();

    }, [team1, team2]
  );

    return (
      <div className="matchPage">
        <h2>Match Page</h2>

        <h4><Link to = {`/match/${match.team1}`}>{match.team1} </Link>
        VS 
        <Link to = {`/match/${match.team2}`}> {match.team2}</Link></h4>

        <h4>Winner : {match.winningTeam}</h4>
        <p>Date : {match.date}</p>
        <p>Match Number : #{match.matchNumber}</p>
        <p>Season : {match.season}</p>
        <p>Player Of the Match: {match.player_of_Match}</p>
        <p>Team 1 Players : {match.team1Players}</p>
        <p>Team 2 Players : {match.team2Players}</p>
        <p>Won By : {match.wonBy}</p>
        <p>Margin: {match.margin}</p>

      </div>
    );
  }

  export default Match;
