import MatchDetailComponent from "../components/matchDetailComponent";
import MatchSmallComponent from "../components/matchSmallComponent";
import "./teamPage.css";
import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { PieChart } from "react-minimal-pie-chart";

function Team() {

  const {teamName} = useParams(); // {} is important otherwise it will go as [object object]

  const [team, setTeam] = useState({recentMatches:[]});

  useEffect(() => {
    const fetchTeam = async () => {
      const response = await fetch(`http://localhost:8081/dashboard/team?team=${teamName}`);
      const data = await response.json();
      setTeam(data);
    };

    fetchTeam();
  }, [teamName]);

  if(!team || !team.recentMatches){return "Data Not Fetched"}

  return (
    <div className="teamPage">

      <div className="name-section">
        <h2 className="name">{team.teamName}</h2>
      </div>

      <div className="teamDetail-section">

        <h4>Total Wins : {team.totalWins}</h4>
        <h4>Total Losses: {team.totalMatches - team.totalWins}</h4>
        <h4>Total Matches: {team.totalMatches}</h4>

        <PieChart></PieChart>

      </div>

      <div className="MDC-section">
        <h3>Latest Matches:</h3>
        <MatchDetailComponent match = {team.recentMatches[0]}/>
      </div>

      {team.recentMatches.slice(1).map( (m) => <MatchSmallComponent match={m}/>)}

      <div className="more-section">
        <h2>more</h2>
      </div>

    </div>
  );
}

export default Team;
