import React, {useState, useEffect} from "react";
import "../styling/matchPage.css";
import {useParams, Link} from "react-router-dom";
import MatchComponent from "../components/matchComponent";
import MatchDetailComponent from "../components/matchDetailComponent";

function Match() {

  const {team1, team2} = useParams();

  const [match, setMatch] = useState('');

  useEffect(
    () => {
      const fetchMatch = async () => {
        const response = await fetch(`http://localhost:8081/dashboard/matchBetween/${team1}/${team2}`);
        const data = await response.json();
        setMatch(data);
      }

    fetchMatch();

    }, [team1, team2]
  );

  if(!match || !match[0]){return "Data Not Fetched"}

    let wonT1 = 0;
    match.map( m => wonT1 = team1===m.winningTeam ? wonT1+1 : wonT1)


    return (
      <div className="matchPage">

        <h2 className="diffLinkCol">
          <Link to = {`/team/${team1}`}>{team1} </Link>
          VS
          <Link to = {`/team/${team2}`}> {team2}</Link>
        </h2>

        <p className={wonT1 > (match.length-wonT1) ? "won-card" : "lost-card"}>{team1} won {wonT1} matches.</p>
        <p className={wonT1 < (match.length-wonT1) ? "won-card" : "lost-card"}>{team2} won {match.length-wonT1} matches.</p>
        <h3>Total Matches: {match.length}</h3>

        {
          match.map( (m) => <MatchComponent match={m}/>)
        }

        {/* {
          match.map( (m) => <MatchDetailComponent match={m}/>)
        } */}

      </div>
    );
  }

  export default Match;
