import React, { useState } from "react";

function MatchTile(props) {

  const teams = props.teams;
  const [selectedTeam1, setSelectedTeam1] = useState(null);
  const [selectedTeam2, setSelectedTeam2] = useState(null);

  if (!teams) {
    return "Data Not Fetched";
  }

  const handleButton = (event) => {
    event.preventDefault(); // Prevent default form submission behavior
    const teamLink = `/match/${selectedTeam1}/${selectedTeam2}`;
    window.location.href = teamLink;
  };

  return (
    <div>

        <label for="team1">Choose team1: </label>
          <select name="team1" id="team1" onChange={(event) => setSelectedTeam1(event.target.value)} >
            {teams.map((team) => (
              <option key={team.teamName} value={team.teamName}>
                {team.teamName}
              </option>
            ))}
          </select>

        <label for="team2">Choose team2: </label>
          <select name="team2"  id="team2" onChange={(event) => setSelectedTeam2(event.target.value)} >
            {teams.filter((team) => team.teamName !== selectedTeam1).map((team) => (
                <option key={team.teamName} value={team.teamName}>
                  {team.teamName}
                </option>
              ))}
          </select>

          <button type="submit" onClick={handleButton}>Submit</button>

          <p>{selectedTeam1}</p>
          <p>{selectedTeam2}</p>

    </div>
  );
}

export default MatchTile;
